"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[477],{10:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"The-Miracle-Morning","metadata":{"permalink":"/blog/The-Miracle-Morning","source":"@site/blog/2023-04-15-the-miracle-morning/2023-04-15-the-miracle-morning.md","title":"\u27ea\u4e0a\u73ed\u524d\u7684\u95dc\u93751\u5c0f\u6642\u27eb \u6d88\u9664\u7126\u616e\uff0c\u627e\u56de\u5c6c\u65bc\u4f60\u7684\u7406\u60f3\u4eba\u751f","description":"\u539f\u672c\u7684\u6211\u5c0d\u65bc\u5f88\u591a\u4e8b\u7269\u90fd\u611f\u5230\u597d\u5947\uff0c\u4e5f\u5f88\u559c\u6b61\u5b78\u7fd2\u4e0d\u540c\u9818\u57df\u7684\u65b0\u77e5 ; \u4f46\u662f\u524d\u9663\u5b50\u610f\u8b58\u5230\u81ea\u5df1\u5c0d\u65bc\u5b78\u7fd2\u4f3c\u4e4e\u4e0d\u518d\u90a3\u9ebc\u71b1\u60c5\uff0c\u5c0d\u65bc\u65b0\u4e8b\u7269\u7684\u597d\u5947\u4e5f\u660e\u986f\u964d\u4f4e\uff0c\u4e26\u4e14\u5bb9\u6613\u6c92\u539f\u56e0\u7684\u611f\u5230\u7126\u616e\u3002","date":"2023-04-15T00:00:00.000Z","formattedDate":"April 15, 2023","tags":[{"label":"\u81ea\u6211\u6210\u9577","permalink":"/blog/tags/\u81ea\u6211\u6210\u9577"},{"label":"\u5fc3\u7406\u5065\u5eb7","permalink":"/blog/tags/\u5fc3\u7406\u5065\u5eb7"}],"readingTime":12.97,"hasTruncateMarker":true,"authors":[{"name":"Mingo","url":"https://github.com/mingohan","imageURL":"/img/logo.png","key":"mingo"}],"frontMatter":{"slug":"The-Miracle-Morning","title":"\u27ea\u4e0a\u73ed\u524d\u7684\u95dc\u93751\u5c0f\u6642\u27eb \u6d88\u9664\u7126\u616e\uff0c\u627e\u56de\u5c6c\u65bc\u4f60\u7684\u7406\u60f3\u4eba\u751f","authors":"mingo","tags":["\u81ea\u6211\u6210\u9577","\u5fc3\u7406\u5065\u5eb7"],"toc_min_heading_level":2,"toc_max_heading_level":2}},"content":"\u539f\u672c\u7684\u6211\u5c0d\u65bc\u5f88\u591a\u4e8b\u7269\u90fd\u611f\u5230\u597d\u5947\uff0c\u4e5f\u5f88\u559c\u6b61\u5b78\u7fd2\u4e0d\u540c\u9818\u57df\u7684\u65b0\u77e5 ; \u4f46\u662f\u524d\u9663\u5b50\u610f\u8b58\u5230\u81ea\u5df1\u5c0d\u65bc\u5b78\u7fd2\u4f3c\u4e4e\u4e0d\u518d\u90a3\u9ebc\u71b1\u60c5\uff0c\u5c0d\u65bc\u65b0\u4e8b\u7269\u7684\u597d\u5947\u4e5f\u660e\u986f\u964d\u4f4e\uff0c\u4e26\u4e14\u5bb9\u6613\u6c92\u539f\u56e0\u7684\u611f\u5230\u7126\u616e\u3002\\n\\n\u56e0\u7232\u4e00\u76f4\u4ee5\u4f86\u90fd\u7b97\u662f\u591c\u8c93\u5b50\uff0c\u539f\u672c\u4ee5\u70ba\u662f\u90a3\u6bb5\u6642\u671f\u7684\u7761\u7720\u4e0d\u8db3\u5c0e\u81f4\u72c0\u614b\u8f03\u5dee\uff0c\u5617\u8a66\u8b93\u81ea\u5df1\u65e9\u4e00\u9ede\u7761\u548c\u5229\u7528\u5047\u65e5\u88dc\u7720\u4f46\u90fd\u6c92\u6709\u986f\u8457\u7684\u6539\u5584\uff0c\u56e0\u6b64\u90a3\u4e00\u9663\u5b50\u4e0d\u505c\u5730\u627e\u5c0b\u6539\u5584\u7684\u65b9\u6cd5\u3002\u5076\u7136\u770b\u5230\u4e86\u9019\u672c\u66f8\u7684\u4ecb\u7d39\uff0c\u5be6\u969b\u5617\u8a66\u4e86\u66f8\u4e2d\u7684\u65b9\u6cd5\uff0c\u6700\u5f8c\u6709\u6210\u529f\u627e\u56de\u81ea\u5df1\u7684\u71b1\u60c5\u8207\u5c0d\u4e8b\u7269\u7684\u597d\u5947\u5fc3\uff0c\u7761\u7720\u54c1\u8cea\u4e5f\u7372\u5f97\u660e\u986f\u6539\u5584\uff0c\u8eab\u5fc3\u72c0\u614b\u4e5f\u9010\u6f38\u56de\u5230\u6b63\u5411\u7684\u90a3\u908a\u3002\\n\\n\u82e5\u4f60\u4e5f\u906d\u9047\u4e00\u76f4\u6709\u7a2e\u672a\u77e5\u7684\u75b2\u5026\u611f\uff0c\u7761\u518d\u591a\u90fd\u4f9d\u7136\u611f\u5230\u75b2\u7d2f\uff0c\u4e0d\u4eff\u5617\u8a66\u770b\u770b\u66f8\u4e2d\u7684\u65b9\u6cd5\uff0c\u85c9\u7531\u91cb\u653e\u5167\u5fc3\u7684\u58d3\u529b\uff0c\u9010\u6b65\u627e\u56de\u5145\u6eff\u6d3b\u529b\u7684\u81ea\u5df1\u3002\\n\\n\x3c!--truncate--\x3e\\n\\n## \u9019\u672c\u66f8\u5728\u8aaa\u4ec0\u9ebc\\n\\n\u4f5c\u8005\u54c8\u723e\u2027\u57c3\u723e\u7f85\u5fb7(Hal Elrod)\xa0\u6b78\u7d0d\u4e866\u500b\u5728\u6668\u9593\u57f7\u884c\u7684 S.A.V.E.R.S. \u62ef\u6551\u4eba\u751f\u516d\u6cd5\uff0c\u53ea\u8981\u4e00\u5c0f\u6642\u4fbf\u53ef\u4ee5\u8b93\u4f60\u627e\u56de\u5c6c\u65bc\u4f60\u7684\u4eba\u751f\uff0c\u8b93\u4f60\u671d\u8457\u4f60\u7684\u7406\u60f3\u4eba\u751f\u9081\u9032\u3002\\n\\n![](./2023-04-15-12-01-34.png)\\n\\n## \u9020\u6210\u5e73\u5eb8\u7684\u539f\u56e0\\n\u70ba\u4ec0\u9ebc\u5b30\u5152\u8a95\u751f\u6642\u6211\u5011\u5e38\u8aaa\u4ed6\u5011\u662f\u300c\u751f\u547d\u7684\u5947\u8e5f\u300d\uff0c\u537b\u53c8\u5bb9\u8a31\u6211\u5011\u81ea\u5df1\u5e73\u5eb8\u5730\u904e\u4e00\u751f\uff1f\\n\\n### 1.\u5f8c\u8996\u93e1\u75c7\u5019\u7fa4\\n\u5f8c\u8996\u93e1\u75c7\u5019\u7fa4\uff08Rearview Mirror Syndrome, RMS)\uff1a\u6211\u5011\u7684\u6f5b\u5728\u5fc3\u7406\u90fd\u6709\u4e00\u9762\u81ea\u6211\u8a2d\u9650\u7684\u5f8c\u8996\u93e1\uff0c\u900f\u904e\u9019\u9762\u93e1\u5b50\uff0c\u6211\u5011\u6703\u4e0d\u65b7\u7de9\u89e3\u8207\u91cd\u5efa\u6211\u5011\u7684\u904e\u53bb\u3002\u6211\u5011\u8aa4\u4ee5\u70ba\u904e\u53bb\u7684\u6211\u5011\u5c31\u662f\u73fe\u5728\u7684\u6211\u5011\uff0c\u5c0e\u81f4\u6211\u5011\u6839\u64da\u904e\u53bb\u7684\u4fb7\u9650\u800c\u9650\u5236\u4e86\u6211\u5011\u73fe\u5728\u7684\u771f\u6b63\u6f5b\u529b\u3002\\n\\n### 2.\u6c92\u6709\u76ee\u6a19\u7684\u4eba\u751f\\n\u7f3a\u4e4f\u4eba\u751f\u76ee\u6a19\u7684\u4eba\uff0c\u53ea\u6703\u904e\u4e00\u5929\u7b97\u4e00\u5929\uff0c\u8ffd\u6c42\u773c\u524d\u77ed\u66ab\u7684\u5feb\u6a02\uff0c\u9003\u907f\u4efb\u4f55\u80fd\u5920\u5e6b\u52a9\u4ed6\u5011\u6210\u9577\u7684\u75db\u82e6\u6216\u4e0d\u90a3\u9ebc\u8212\u670d\u7684\u4e8b\u3002 \\n\\n**\u5e73\u5eb8\u4e0d\u662f\u4f60\u548c\u4ed6\u4eba\u76f8\u4e92\u6bd4\u8f03\u7684\u7d50\u679c\uff0c\u800c\u662f\u4f60\u6c92\u6709\u4e0b\u6c7a\u5fc3\u4e0d\u65b7\u5730\u5b78\u7fd2\u3001\u6210\u9577\u8207\u9032\u6b65\u3002**\\n\\n### 3.\u5b64\u7acb\u4e8b\u4ef6\\n\u6211\u5011\u8aa4\u4ee5\u70ba\u6211\u5011\u6240\u505a\u7684\u6bcf\u4e00\u500b\u9078\u64c7\uff0c\u4ee5\u53ca\u6211\u5011\u6240\u63a1\u53d6\u7684\u500b\u5225\u884c\u52d5\uff0c\u90fd\u53ea\u5f71\u97ff\u67d0\u500b\u7279\u5b9a\u6642\u9593\u6216\u60c5\u6cc1\u3002\u4f46\u4e8b\u5be6\u662f\uff0c\u4f60\u7684\u6bcf\u4e00\u500b\u60f3\u6cd5\u3001\u6289\u64c7\u8207\u884c\u52d5\u90fd\u6c7a\u5b9a\u6211\u5011\u5c07\u6210\u70ba\u600e\u6a23\u7684\u4eba\u3002\u4f60\u505a\u4efb\u4f55\u4e8b\u7684\u614b\u5ea6\u5c31\u662f\u4f60\u505a\u6240\u6709\u4e8b\u60c5\u7684\u614b\u5ea6\u3002\\n\\n### 4.\u7f3a\u4e4f\u8feb\u5207\u611f \\n**\u6211\u5011\u7e3d\u662f\u82b1\u5f88\u591a\u6642\u9593\u601d\u8003\u6211\u5011\u61c9\u8a72\u63a1\u53d6\u4ec0\u9ebc\u884c\u52d5\u624d\u80fd\u5275\u9020\u6211\u5011\u60f3\u8981\u7684\u6210\u679c\uff0c\u4f46\u6211\u5011\u537b\u4e0d\u53bb\u884c\u52d5\u3002**\\n\\n\u52d5\u6a5f\u4f7f\u4f60\u5c55\u958b\u884c\u52d5\uff0c\u7fd2\u6163\u8b93\u4f60\u6301\u7e8c\u524d\u9032\u3002\\n\\n### 5.\u7f3a\u4e4f\u81ea\u6211\u6210\u9577\\n\u6210\u529f\u7684\u4eba\u4e26\u975e\u4e00\u751f\u4e0b\u4f86\u5c31\u6210\u529f\uff0c\u4ed6\u5011\u662f\u990a\u6210\u4e0d\u6210\u529f\u7684\u4eba\u4e0d\u559c\u6b61\u505a\u7684\u826f\u597d\u7fd2\u6163\u800c\u6210\u529f\u3002\u6210\u529f\u7684\u4eba\u4e5f\u4e0d\u4e00\u5b9a\u5c31\u559c\u6b61\u505a\u9019\u4e9b\u4e8b\uff1b\u4ed6\u5011\u53ea\u662f\u8b93\u4ed6\u7fd2\u6163\u6210\u81ea\u7136\u3002\\n\\n\u4eba\u751f\u4e0d\u662f\u4f46\u9858\u4f60\u80fd\u9054\u5230\u67d0\u7a2e\u6210\u5c31\uff0c\u6216\u662f\u6210\u70ba\u67d0\u500b\u5049\u4eba\uff0c**\u4eba\u751f\u662f\u559c\u6b61\u4f60\u7684\u73fe\u5728\uff0c\u611b\u73fe\u5728\u7684\u4f60\uff0c\u7136\u5f8c\u4e0d\u65b7\u5730\u6539\u5584\u5b83\u5011\u3002**\\n\\n\u8981\u77e5\u9053\u76ee\u524d\u7684\u8655\u5883\u662f\u66ab\u6642\u7684\uff0c\u4e26\u4e14\u8981\u77e5\u9053\u4f60\u8a72\u5f80\u4f55\u8655\u53bb\u3002\u73fe\u5728\u7684\u4f60\u61c9\u8a72\u77e5\u9053\u5fc5\u9808\u5b78\u7fd2\u4ec0\u9ebc\uff0c\u4f60\u624d\u80fd\u6210\u70ba\u5275\u9020\u4f60\u6240\u5922\u60f3\u7684\u4e00\u5207\u7684\u90a3\u500b\u4eba\u3002\u4f60\u7684\u672a\u4f86\u9060\u6bd4\u4f60\u7684\u73fe\u5728\u66f4\u91cd\u8981\uff0c\u4f46\u4f60\u73fe\u5728\u6240\u505a\u7684\u4e00\u5207\u5c07\u6c7a\u5b9a\u4f60\u7684\u672a\u4f86\u3002\\n\\n## \u5982\u4f55\u589e\u52a0\u8d77\u5e8a\u7684\u52d5\u6a5f\uff1f \\n### \u554f\u554f\u81ea\u5df1\u4f60\u4eca\u5929\u65e9\u6668\u70ba\u4ec0\u9ebc\u8d77\u5e8a\uff1f\\n\u5982\u679c\u4f60\u60f3\u5e36\u8457\u6eff\u8db3\u611f\u4e0a\u5e8a\u7761\u89ba\uff0c\u6bcf\u5929\u65e9\u6668\u5c31\u5fc5\u9808\u4ee5\u5805\u5b9a\u7684\u6c7a\u5fc3\u8d77\u5e8a\u3002\\n## \u65e9\u8d77\u7684\u5c0f\u6487\u6b65\\n### 1.\u7761\u524d\u5efa\u7acb\u597d\u5fc3\u614b\\n\u5728\u7761\u524d\u5c31\u9810\u5148\u5efa\u8a2d\u5fc3\u7406\uff0c\u6697\u793a\u81ea\u5df1\u5c07\u4ee5\u6b63\u9762\u7684\u5fc3\u614b\u8fce\u63a5\u660e\u5929\u7684\u5230\u4f86\u3002\\n### 2.\u671f\u5f85\u660e\u5929\u65e9\u6668\u7684\u5230\u4f86\\n\u5c31\u50cf\u662f\u5c0f\u6642\u5019\uff0c\u5728\u7761\u524d\u671f\u5f85\u9694\u5929\u7684\u9060\u8db3\u822c\uff0c\u8208\u596e\u7684\u60f3\u8457\u660e\u5929\u7684\u65c5\u7a0b\uff0c\u60f3\u8981\u9694\u5929\u4e00\u65e9\u7acb\u523b\u8df3\u4e0b\u5e8a\u8fce\u63a5\u660e\u5929\u7684\u5230\u4f86\u3002\\n### 3.\u9b27\u9418\u64fa\u9060\u4e00\u9ede\\n\u8b93\u81ea\u5df1\u5fc5\u9808\u4e0b\u5e8a\u95dc\u9b27\u9418\uff0c\u6709\u6d3b\u52d5\u5c31\u6703\u6709\u7cbe\u795e!\\n### 4.\u5237\u7259 \uff06 \u6d17\u81c9\\n\u85c9\u7531\u5916\u754c\u7684\u523a\u6fc0\u559a\u9192\u4f60\u7684\u8eab\u9ad4\u3002\\n### 5.\u559d\u4e00\u676f\u6c34\\n\u70ba\u4f60\u7d93\u904e\u4e00\u665a\u7761\u7720\u7684\u8eab\u9ad4\u88dc\u5145\u6c34\u5206\u3002\\n### 6.\u63db\u4e0a\u4f60\u7684\u904b\u52d5\u670d\\n\u9069\u5ea6\u7684\u904b\u52d5\uff0c\u6d41\u9ede\u6c57\u518d\u6dcb\u6d74\uff0c\u795e\u6e05\u6c23\u723d\u5730\u8fce\u63a5\u5c6c\u65bc\u4f60\u7684\u65e9\u6668\u3002\\n\\n## \u5efa\u69cb\u81ea\u6211\u80af\u5b9a\u7684\u6b65\u9a5f\\n\u9664\u975e\u6f5b\u610f\u8b58\u4e2d\u6df1\u4fe1\u4f60\u6703\u6210\u529f\u5426\u5247\u7d42\u5c07\u5931\u6557\u3002\\n\\n### 1.\u4f60\u771f\u6b63\u60f3\u8981\u7684\u662f\u4ec0\u9ebc\\n\u5beb\u4e0b\u4f60\u771f\u6b63\u60f3\u8981\u9054\u6210\u7684\u9858\u666f\u6216\u751f\u6d3b\u72c0\u614b\uff0c\u6e05\u695a\u5730\u5beb\u4e0b\u4f60\u8a8d\u7232\u7406\u60f3\u4eba\u751f\u7684\u6a21\u6a23\u3002\\n### 2.\u4f60\u70ba\u4ec0\u9ebc\u60f3\u8981\\n\u4f60\u60f3\u6539\u8b8a\u6216\u671d\u8457\u9019\u500b\u76ee\u6a19\u524d\u9032\u7684\u52d5\u6a5f\u70ba\u4f55\uff1f\u627e\u51fa\u85cf\u5728\u4f60\u5167\u5fc3\u6df1\u8655\u7684\u300c\u70ba\u4ec0\u9ebc\u300d\u3002\\n### 3.\u4e0b\u5b9a\u6c7a\u5fc3\u6210\u70ba\u90a3\u500b\u6210\u529f\u7684\u4f60\\n\u53ea\u6709\u7576\u4f60\u6295\u8cc7\u7121\u6578\u6642\u9593\u4f7f\u81ea\u5df1\u9032\u6b65\u4e4b\u5f8c\uff0c\u4f60\u7684\u5916\u5728\u4e16\u754c\u624d\u6703\u6539\u5584\u3002\\n### 4.\u4f60\u8981\u505a\u4ec0\u9ebc\u624d\u80fd\u6210\u529f\\n\u5beb\u4e0b\u4f60\u61c9\u8a72\u63a1\u53d6\u7684\u884c\u52d5\u8207\u8a08\u756b\u3002\\n\u5beb\u4e0b\u5177\u9ad4\u7684\u884c\u52d5(\u983b\u7387\u3001\u6b21\u6578\u3001\u78ba\u5207\u7684\u6642\u9593)\uff0c\u525b\u958b\u59cb\u4e0d\u80fd\u8caa\u591a\uff0c\u8981\u4ee5\u4f60\u80fd\u52dd\u4efb\u7684\u65b9\u5f0f\u9032\u884c\u3002 (\u4f8b\u5982\uff1a\u6211\u6bcf\u9031\u8981\u65bc\u65e9\u66686\u9ede\uff5e7\u9ede\u9593\u6162\u8dd13\u6b21\u4ee5\u4e0a\uff0c\u6bcf\u6b21\u81f3\u5c1130\u5206\u9418)   \\n### 5.\u5584\u52a0\u5229\u7528\u80fd\u6fc0\u52f5\u4f60\u7684\u8a9e\u9304\u548c\u54f2\u7406\\n\u85c9\u7531\u6fc0\u52f5\u4eba\u5fc3\u7684\u6587\u7ae0\u6216\u5f71\u7247\u4f86\u88dc\u5145\u5167\u5fc3\u7684\u80fd\u91cf\uff0c\\n\\n## \u66f8\u5beb\u65e5\u8a18\u7684\u6548\u679c\\n\u6bcf\u5929\u5beb\u65e5\u8a18\uff0c\u4ee5\u4e00\u7a2e\u6709\u898f\u5283\u3001\u6709\u7b56\u7565\u7684\u65b9\u5f0f\u5beb\u65e5\u8a18\u53bb\u5f15\u5c0e\u4f60\u7684\u5c08\u6ce8\u5728\u4f60\u7684\u6210\u679c\u3001\u4f60\u7684\u611f\u6069\uff0c\u4ee5\u53ca\u660e\u5929\u8981\u505a\u5f97\u66f4\u597d\u7684\u627f\u8afe\uff0c\u9019\u6a23\u4fbf\u53ef\u66f4\u4eab\u53d7\u4f60\u7684\u6bcf\u4e00\u5929\uff0c\u5c0d\u4f60\u6bcf\u5929\u7684\u9032\u6b65\u5fc3\u751f\u6b61\u559c\uff0c\u8166\u7b4b\u66f4\u6e05\u695a\u5730\u63d0\u5347\u4f60\u7684\u6210\u5c31\u3002\\n\\n\u85c9\u7531\u65e5\u8a18\u7684\u53cd\u601d\uff0c\u53ef\u4ee5\u4e86\u89e3\u81ea\u5df1\u7684\u9032\u6b65\u8207\u4e0d\u8db3\u3002\\n\\n### \u6b63\u9762\u6548\u61c9\\n\u85c9\u7531\u65e5\u8a18\u7684\u7d00\u9304\uff0c\u53ef\u4ee5\u89c0\u5bdf\u5230\u81ea\u5df1\u7684\u9032\u6b65\u8207\u6210\u9577\u7684\u8ecc\u8de1\uff0c\u662f\u4e00\u4ef6\u8b93\u4eba\u611f\u5230\u5feb\u6a02\u7684\u4e8b\uff0c\u9019\u4efd\u559c\u6085\u6703\u8f49\u5316\u70ba\u7e7c\u7e8c\u9032\u6b65\u7684\u52d5\u529b\u3002\\n\\n### \u8ca0\u9762\u6548\u679c\\n#### \u61c9\u907f\u514d\u904e\u5ea6\u95dc\u6ce8\u843d\u5dee(\u4e0d\u8db3\u7684\u90e8\u5206)\\n\u904e\u5ea6\u95dc\u6ce8\u843d\u5dee\u5c0d\u6211\u5011\u7684\u4fe1\u5fc3\u548c\u81ea\u6211\u5f62\u8c61\u6709\u5bb3\uff0c\u6703\u4f7f\u6211\u5011\u89ba\u5f97\u64c1\u6709\u7684\u4e0d\u5920\u591a\uff0c\u6210\u5c31\u4e0d\u8db3 ; \u9054\u6210\u7684\u7d50\u679c\u4e0d\u5920\u597d\uff0c\u6216\u6c92\u6709\u81ea\u5df1\u300c\u9810\u671f\u300d\u7684\u90a3\u9ebc\u597d\u3002\u9019\u7a2e\u8ca0\u9762\u7684\u60f3\u6cd5\u6703\u8017\u76e1\u6211\u5011\u7684\u80fd\u91cf\uff0c\u963b\u7919\u6211\u5011\u7684\u9032\u6b65\u3002\\n\\n\u61c9\u5c07\u9019\u4e9b\u843d\u5dee\u6216\u4e0d\u8db3\u8f49\u5316\u70ba\u81ea\u5df1\u9084\u80fd\u9032\u6b65\u7684\u7a7a\u9593\uff0c\u4e0d\u9700\u56e0\u6b64\u89ba\u5f97\u81ea\u5df1\u505a\u7684\u4e0d\u5920\u597d\u3002\\n\\n\u53ef\u4ee5\u60f3\u50cf\u8457\u81ea\u5df1\u6301\u7e8c\u9032\u6b65\u5f8c\uff0c\u6700\u7d42\u9054\u6210\u81ea\u8eab\u76ee\u6a19\u7684\u6a21\u6a23 ; \u601d\u8003\u70ba\u4e86\u9054\u6210\u76ee\u6a19\uff0c\u81ea\u5df1\u9084\u9700\u8981\u63a1\u53d6\u4ec0\u9ebc\u884c\u52d5\u6216\u898f\u5283\uff0c\u5982\u6b64\u7684\u95dc\u6ce8\u843d\u5dee\u624d\u6703\u662f\u5065\u5eb7\u6709\u76ca\u7684\u3002\\n\\n## \u672c\u66f8\u6536\u7a6b\\n\u7531\u65bc\u8f49\u8077\u7684\u7de3\u6545\uff0c\u6709\u8a31\u591a\u7684\u57fa\u672c\u529f\u8981\u5f4c\u88dc\uff0c\u56e0\u6b64\u7e3d\u611f\u89ba\u6709\u88dc\u4e0d\u5b8c\u7684\u5de8\u5927\u77e5\u8b58\u7f3a\u53e3\uff0c\u53c8\u56e0\u70ba\u6c92\u6709\u5beb\u65e5\u8a18\u6216\u8f38\u51fa\u6587\u5b57\u7684\u7fd2\u6163\uff0c**\u6c92\u6709\u9069\u6642\u7684\u5c07\u7126\u616e\u7684\u539f\u56e0\u6216\u5fc3\u7406\u72c0\u614b\u5177\u73fe\u5316(\u6587\u5b57\u5316)**\uff0c\u65bc\u662f\u90a3\u4efd\u7126\u616e\u6703\u9010\u6f38\u64f4\u5927\uff0c\u6700\u5f8c\u4f60\u9023\u81ea\u5df1\u7126\u616e\u7684\u539f\u56e0\u90fd\u5f04\u4e0d\u6e05\u695a\uff0c\u4f46\u662f\u90a3\u4efd\u7121\u5f62\u7684\u7126\u616e\u537b\u6703\u6301\u7e8c\u5730\u5f71\u97ff\u8457\u4f60\u3002\\n\\n\u9019\u4efd\u7126\u616e\u611f\uff0c\u6703\u4f7f\u4f60\u7684\u610f\u8b58\u53ea\u805a\u7126\u5728\u4f60\u9084\u4e0d\u61c2\u7684\u4e8b\u7269\u4e0a\uff0c\u65bc\u662f\u66f4\u52a9\u9577\u4e86\u300c\u8cc7\u8a0a\u7126\u616e\u300d\u7684\u6301\u7e8c\u64f4\u5927\uff0c\u300c\u7e3d\u611f\u89ba\u81ea\u5df1\u4ec0\u9ebc\u90fd\u4e0d\u6703\u300d\uff0c\u9019\u7a2e\u81ea\u6211\u5426\u5b9a\u7684\u8ca0\u9762\u5fc3\u7406\u6703\u6f38\u6f38\u5f71\u97ff\u81ea\u5df1\u7684\u8eab\u5fc3\u72c0\u614b\u3002\\n \\n\u85c9\u7531\u5be6\u969b\u5617\u8a66\u66f8\u4e2d\u7684\u65b9\u6cd5\uff0c\u7d93\u904e\u5c07\u8fd1\u5169\u9031\u7684\u8abf\u6574\uff0c\u6bcf\u5929\u6bd4\u5e73\u6642\u65e9\u8d77 5~10\u5206\u9418 \uff0c\u6700\u7d42\u6211\u6210\u529f\u5730\u5c07\u8d77\u5e8a\u6642\u9593\u63d0\u524d 2 ~ 3 \u5c0f\u6642\uff0c\u8d77\u5e8a\u5f8c\u4e5f\u4e0d\u518d\u662f\u5306\u5306\u5fd9\u5fd9\u5730\u53bb\u4e0a\u73ed\uff0c\u800c\u662f\u53ef\u4ee5\u908a\u6668\u8dd1\u908a\u807d\u8457Podcast\uff0c\u66f4\u65b0\u81ea\u5df1\u95dc\u6ce8Youtuber\u7684\u65b0\u5f71\u7247\u6216\u662f\u7684\u65b0\u7684\u6280\u8853\uff0c\u4ee5\u53ca\u6709\u984d\u5916\u7684\u6642\u9593\u53ef\u4ee5\u601d\u8003\uff0c\u5982\u4f55\u5c07\u6700\u8fd1\u95b1\u8b80\u6216\u5f71\u7247\u5b78\u5230\u7684\u6280\u5de7\u6216\u60f3\u6cd5\u61c9\u7528\u5728\u4eca\u5929\u7684\u5de5\u4f5c\u4e2d\u3002\\n\\n\u539f\u672c\u662f\u591c\u8c93\u5b50\u7684\u6211\uff0c\u5f8c\u4f86\u8f49\u8b8a\u6210\u4eab\u53d7\u65e9\u8d77\u7684\u611f\u89ba\uff0c\u9019\u4ef6\u4e8b\u4e5f\u8b93\u6211\u610f\u8b58\u5230\uff0c\u539f\u4f86\u770b\u4f3c\u5fae\u5c0f\u7684\u6539\u8b8a(\u6bcf\u5929\u53ea\u63d0\u65e9\u5e7e\u5206\u9418\u8d77\u5e8a)\uff0c \u82e5\u7d93\u904e\u81ea\u5df1\u523b\u610f\u7684\u5b89\u6392\u8207\u898f\u5283\uff0c\u6700\u5f8c\u4e5f\u80fd\u653e\u5927\u5230\u6709\u986f\u8457\u7684\u6210\u6548\u3002 \\n\\n\u85c9\u7531\u65e9\u6668\u7684\u6642\u9593\uff0c\u8b93\u6211\u80fd\u5920\u5fc3\u7121\u65c1\u9a16\u7684\u597d\u597d\u7684\u601d\u8003\u8207\u5b9a\u7fa9\u81ea\u5df1\u9047\u5230\u7684\u554f\u984c\uff0c**\u4e00\u4f46\u80fd\u5920\u5b9a\u7fa9\u9047\u5230\u7684\u554f\u984c\uff0c\u63a5\u4e0b\u4f86\u5c31\u80fd\u627e\u5c0b\u89e3\u6c7a\u7684\u65b9\u6cd5\u6216\u6539\u5584\u7684\u65b9\u5411(\u6709\u4e86\u65b9\u5411\u8207\u76ee\u6a19\u4fbf\u80fd\u5920\u9650\u7e2e\u554f\u984c\u7bc4\u570d\uff0c\u964d\u4f4e\u7126\u616e\u611f)\u3002**\\n\\n\u6211\u8a8d\u70ba\u672c\u66f8\u5c0d\u6211\u6700\u6709\u555f\u767c\u7684\u5730\u65b9\u662f\uff0c**\u4f60\u5fc5\u9808\u8981\u4fdd\u7559\u4e00\u6bb5\u4e0d\u88ab\u5176\u4ed6\u4e8b\u7269\u6253\u64fe\u7684\u6642\u9593\u7d66\u81ea\u5df1\uff0c\u9019\u6bb5\u6642\u9593\u662f\u4f60\u8207\u81ea\u5df1\u5c0d\u8a71\u7684\u795e\u8056\u6642\u9593** \uff0c\u53ef\u4ee5\u554f\u554f\u81ea\u5df1\u6700\u8fd1\u9047\u5230\u4e86\u4ec0\u9ebc\u958b\u5fc3\u6216\u662f\u611f\u6069\u7684\u4e8b ; \u6216\u662f\u9047\u5230\u4e86\u4ec0\u9ebc\u56f0\u96e3\uff0c\u60f3\u8981\u63a1\u53d6\u4ec0\u9ebc\u884c\u52d5\u6216\u627e\u8ab0\u5c0b\u6c42\u5354\u52a9\uff0c**\u85c9\u7531\u6709\u610f\u8b58\u5730\u5bdf\u89ba\u81ea\u5df1\u8fd1\u671f\u7684\u72c0\u614b\uff0c\u53ef\u4ee5\u6709\u6548\u7684\u5e6b\u52a9\u81ea\u5df1\u812b\u96e2\u8ca0\u9762\u7684\u8eab\u5fc3\u72c0\u614b**\u3002\\n\\n\u5c0d\u65bc\u591c\u8c93\u5b50\u7684\u5925\u4f34\u4f86\u8aaa\uff0c\u8981\u7acb\u5373\u990a\u6210\u65e9\u8d77\u7fd2\u6163\u4e26\u4e0d\u5bb9\u6613 ; \u56e0\u6b64\uff0c\u500b\u4eba\u8a8d\u70ba\u521d\u671f\u4e5f\u53ef\u4ee5\u5148\u5728\u665a\u4e0a\u57f7\u884c\uff0c\u53ea\u8981\u78ba\u5b9a\u6709\u9810\u7559\u4e00\u6bb5\u8207\u81ea\u5df1\u5c0d\u8a71\u7684\u6642\u9593\u5373\u53ef\uff0c\u63a5\u8457\u518d\u6162\u6162\u8abf\u6574\u81ea\u5df1\u7684\u4f5c\u606f\u3002\\n\\n\u7531\u65bc\u4f60\u6642\u5e38\u95dc\u6ce8\u81ea\u5df1\u7684\u8eab\u5fc3\u72c0\u614b\uff0c**\u5728\u81ea\u5df1\u72c0\u614b\u4e0d\u597d\u6642\uff0c\u4e5f\u80fd\u61c2\u5f97\u9069\u7576\u7684\u653e\u904e\u81ea\u5df1**\uff0c\u907f\u514d\u518d\u62ff\u96e3\u4ee5\u9054\u6a19\u7684\u81ea\u6211\u8981\u6c42\uff0c\u4f86\u82db\u8cac\u6b63\u5728\u8655\u65bc\u8ca0\u9762\u72c0\u614b\u7684\u81ea\u5df1\u3002\\n\\n\u4eba\u751f\u5982\u540c\u4e00\u5834\u99ac\u62c9\u677e\uff0c\u4e00\u6642\u7684\u843d\u5f8c\u5728\u4e00\u751f\u7684\u6642\u9593\u9577\u5ea6\u4e2d\uff0c\u4e5f\u986f\u5f97\u4e0d\u90a3\u9ebc\u56b4\u91cd ; \u6700\u91cd\u8981\u7684\u662f\uff0c**\u4f60\u5fc5\u9808\u9858\u610f\u76f8\u4fe1\u81ea\u5df1\uff0c\u4ee5\u53ca\u6301\u7e8c\u5730\u89c0\u5bdf\u8207\u4fee\u6b63\u81ea\u5df1\u7684\u6b65\u4f10\uff0c\u5982\u6b64\u4fbf\u80fd\u7e7c\u7e8c\u671d\u5411\u81ea\u5df1\u7684\u7406\u60f3\u4eba\u751f\u9081\u9032\u3002**"}]}')}}]);